mapper.select:
select 

from sys_spe_power
where user_code = #{userCode} and spe_num=#{speNum}

mapper.insert:
insert into sys_spe_power (id, user_code, spe_num, 
  spe_name, spe_key, spe_val, 
  spe_type, spe_use, spe_remark
  )
values (#{id,jdbcType=BIGINT}, #{userCode,jdbcType=VARCHAR}, #{speNum,jdbcType=VARCHAR}, 
  #{speName,jdbcType=VARCHAR}, #{speKey,jdbcType=VARCHAR}, #{speVal,jdbcType=VARCHAR}, 
  #{speType,jdbcType=VARCHAR}, #{speUse,jdbcType=VARCHAR}, #{speRemark,jdbcType=VARCHAR}
  )
