mapper.insert:
insert into sp_alert_notify_tmpl (id, server_id, tmpl_code,
ext_1, ext_2, ext_3, ext_4,
ext_5, ext_6, ext_7, status, tmpl_conent
)
values (#{id,jdbcType=BIGINT}, #{serverId,jdbcType=BIGINT},
#{tmplCode,jdbcType=VARCHAR},
#{ext1,jdbcType=VARCHAR}, #{ext2,jdbcType=VARCHAR}, #{ext3,jdbcType=VARCHAR},
#{ext4,jdbcType=VARCHAR},
#{ext5,jdbcType=VARCHAR}, #{ext6,jdbcType=VARCHAR}, #{ext7,jdbcType=VARCHAR}, #{status,jdbcType=BIT}, #{tmplConent,jdbcType=LONGVARCHAR}
)

mapper.select:
select

,

from sp_alert_notify_tmpl
where 1 = 1

		and server_id = #{serverId,jdbcType=BIGINT}


		and tmpl_code = #{tmplCode,jdbcType=VARCHAR}


		and ext_1 = #{ext1,jdbcType=VARCHAR}


		and ext_2 = #{ext2,jdbcType=VARCHAR}


		and ext_3 = #{ext3,jdbcType=VARCHAR}


		and ext_4 = #{ext4,jdbcType=VARCHAR}


		and ext_5 = #{ext5,jdbcType=VARCHAR}


		and ext_6 = #{ext6,jdbcType=VARCHAR}


		and ext_7 = #{ext6,jdbcType=VARCHAR}


		and status = #{status,jdbcType=BIT}
