mapper.delete:
  delete from sp_property
  where id in 

		  #{id}

mapper.select:
SELECT x.id,x.code,x.name,x.type,x.typename,x.ip,x.fix_time, x.state ,x.statename,x.created_time ,GROUP_CONCAT(distinct x.apply_projects ORDER BY x.apply_projects separator ',') apply_projects,ifnull(x.status,0) is_monitor,x.username,x.usercode from ( SELECT a.id, a.code,a.name,a.type,f.parm_name typename, a.ip, a.fix_time, a.state, d.parm_name statename, a.created_time,c.apply_projects,b.status,e.name username ,b.username usercode from sp_property a LEFT JOIN sp_device_admin b on a.ip = b.ip and a.code=b.server_code LEFT JOIN sp_server_admin c on a.ip = c.ip LEFT JOIN sys_parameter d on a.state = d.parm_code and d.parm_type='serviceStatus' LEFT JOIN sp_user e on b.username = e.user_code LEFT JOIN sys_parameter f on a.type = f.parm_code and f.parm_type='serviceType' where 1 = 1
and a.name like CONCAT('%',#{name},'%')
and a.type = #{type}
and a.state = #{state}
and b.status = #{isMonitor}
) x GROUP BY x.code

   	select count(1) from sp_property
    where 1 = 1 

			and id != #{id}


			and code = #{code}

SELECT count(1) from sp_server_notify_log where server_id in ( 
SELECT a.id from sp_server_admin a LEFT JOIN sp_property b on a.ip = b.ip where b.id = #{id}
) and DATE(created_time)=CURDATE()

SELECT a.id from sp_device_admin a 
LEFT JOIN sp_property b on a.ip = b.ip and b.code=a.server_code 
where b.id = #{id}

   	select count(1) from sp_property
    where 1 = 1 

			and ip = #{ip}
