mapper.select:
SELECT * from sp_user_action_detail

	 id=#{id} 
	and usr_act_id=#{usrActId}
	and action_type=#{actionType}
	and action_sql_id=#{actionSqlId}
	and sql_params=#{sqlParams}

SELECT COUNT(id) from sp_user_action_detail

	 id=#{id} 
	and usr_act_id=#{usrActId}
	and action_type=#{actionType}
	and action_sql_id=#{actionSqlId}
	and sql_params=#{sqlParams}

mapper.update:
update sp_user_action_detail
set usr_act_id = #{usrActId,jdbcType=BIGINT},
  action_type = #{actionType,jdbcType=VARCHAR},
  action_sql_id = #{actionSqlId,jdbcType=LONGVARCHAR},
  sql_params = #{sqlParams,jdbcType=LONGVARCHAR}
where id = #{id,jdbcType=BIGINT}

update sp_user_action_detail
set usr_act_id = #{usrActId,jdbcType=BIGINT},
  action_type = #{actionType,jdbcType=VARCHAR}
where id = #{id,jdbcType=BIGINT}
