mapper.select:
  SELECT COUNT(id) from sp_param_alert  

             and id=#{id} 
			and server_id=#{serverId}
			and param_name=#{paramName}
			and param_alias=#{paramAlias}
			and alter_value=#{alterValue}
			and alert_code=#{alert_code}
			and alert_type=#{alertType}
			  and status=#{status} 
			  and created_time >=#{topTime} 
			  and created_time < =#{endTime}

     SELECT * from sp_param_alert  

